declare namespace SwaggerGen {
    interface RouteParameter {
        name: string,
        required: boolean,
        in: 'path',
        type: 'string'
    }

    interface Route {
        httpRoute: string
        method: string
        params: RouteParameter[]
    }

    interface Files {
        swaggerHeader: any
        route: {
            header: any,
            body: any,
            response: any,
        }
    }
}