export namespace PlataEnvLoader {
    export async function load(): PlataPromise<Dictionary<string | undefined>> {
        if (Plata.ProjectJson.plata_build_env !== undefined) {
            return require(`@@/envs/${Plata.ProjectJson.plata_build_env}.env`)
        }

        const configEnv: EnvConfig = require('@@/configs/env').envConfig

        return require(`@@/envs/${configEnv.env}.env`)
    }
}